number = input("Enter a number: ")
reversed_number = ""

for i in range(len(number) - 1, -1, -1):
    reversed_number += number[i] 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
print("Reversed number:", reversed_number)